mapper.update:
UPDATE task_executions
SET status = #{status, typeHandler=com.witchshop.sharedlib.typehandler.TaskStatusesTypeHandler}
WHERE id = #{id, typeHandler=com.witchshop.sharedlib.typehandler.UUIDTypeHandler}

UPDATE task_executions
SET worker_id = #{workerId, typeHandler=com.witchshop.sharedlib.typehandler.UUIDTypeHandler}
WHERE id = #{taskId, typeHandler=com.witchshop.sharedlib.typehandler.UUIDTypeHandler}

UPDATE task_executions
SET status = #{status, typeHandler=com.witchshop.sharedlib.typehandler.TaskStatusesTypeHandler},
    completed_at = NOW(),
    result_data = #{result}
WHERE id = #{id, typeHandler=com.witchshop.sharedlib.typehandler.UUIDTypeHandler}
